package com.selenium.project;

import java.util.regex.Pattern;
import java.util.concurrent.TimeUnit;
import org.junit.*;
import static org.junit.Assert.*;
import static org.hamcrest.CoreMatchers.*;
import org.openqa.selenium.*;
import org.openqa.selenium.firefox.FirefoxDriver;
import org.openqa.selenium.support.ui.Select;

public class FillSurveyTest {
  private WebDriver driver;
  private String baseUrl;
  private boolean acceptNextAlert = true;
  private StringBuffer verificationErrors = new StringBuffer();

  @Before
  public void setUp() throws Exception {
    driver = new FirefoxDriver();
    baseUrl = "http://student.agh.edu.pl/";
    driver.manage().timeouts().implicitlyWait(30, TimeUnit.SECONDS);
  }

  @Test
  public void testSelenium2() throws Exception {
    driver.get(baseUrl + "/~kolaczyk/Survey/fillSurvey.php?surveyID=3");
    assertEquals("Witamy w systamie.", driver.findElement(By.cssSelector("div.panel-heading")).getText());
    driver.findElement(By.cssSelector("input.btn.btn-success")).click();
    assertEquals("Ocena produkt√≥w.", driver.findElement(By.cssSelector("div.panel-heading")).getText());
    driver.findElement(By.cssSelector("input.btn.btn-default")).click();
    try {
      assertTrue(isElementPresent(By.xpath("//img[contains(@src,'data:image/jpeg;base64,')]")));
    } catch (Error e) {
      verificationErrors.append(e.toString());
    }
    try {
      assertTrue(isElementPresent(By.cssSelector("img[alt=\"no image\"]")));
    } catch (Error e) {
      verificationErrors.append(e.toString());
    }
    driver.findElement(By.cssSelector("input.btn.btn-default")).click();
    assertEquals("fdesljfksgb,c.mg gbjnm,dgx bbx lz", driver.findElement(By.xpath("//div[3]/div[3]")).getText());
    driver.findElement(By.cssSelector("input.btn.btn-default")).click();
    driver.findElement(By.cssSelector("input.btn.btn-default")).click();
    driver.findElement(By.cssSelector("input.btn.btn-default")).click();
    driver.findElement(By.cssSelector("input.btn.btn-default")).click();
    driver.findElement(By.cssSelector("input.btn.btn-default")).click();
    driver.findElement(By.cssSelector("input.btn.btn-default")).click();
    assertEquals("Gratulacje ankieta zostala ukonczona", driver.findElement(By.cssSelector("div.panel-body.text-center")).getText());
  }

  @After
  public void tearDown() throws Exception {
    driver.quit();
    String verificationErrorString = verificationErrors.toString();
    if (!"".equals(verificationErrorString)) {
      fail(verificationErrorString);
    }
  }

  private boolean isElementPresent(By by) {
    try {
      driver.findElement(by);
      return true;
    } catch (NoSuchElementException e) {
      return false;
    }
  }

  private boolean isAlertPresent() {
    try {
      driver.switchTo().alert();
      return true;
    } catch (NoAlertPresentException e) {
      return false;
    }
  }

  private String closeAlertAndGetItsText() {
    try {
      Alert alert = driver.switchTo().alert();
      String alertText = alert.getText();
      if (acceptNextAlert) {
        alert.accept();
      } else {
        alert.dismiss();
      }
      return alertText;
    } finally {
      acceptNextAlert = true;
    }
  }
}
